thisTask: t.4;

if( (home.value != null).and(home.value != []) ){
	def toFinish = findTask( {it._id.eq(home.value[0])} )
	if(toFinish != null){
		finishTask(toFinish)
	}
}

make show, visible on thisTask when {true}
change viewTitle value { "Žiadne" }
change previous value { null }
change home value { [] }

